sys_script_include.script:
var DomainToolsConfiguration = Class.create();
DomainToolsConfiguration.prototype = {
    initialize: function() {
    },

	getConfigurationByIntegId : function(integrationId) {
		gs.info("I am here");
		var config = {
			userName : '',
			apiKey : ''
		};
		var c = new GlideRecord("sn_sec_core_integration_item_config");
		c.addQuery("integration", integrationId);
		c.addQuery('name', 'apiKey').addOrCondition('name', 'userName');
		c.query();
		while (c.next()) {
			if (c.getValue('name') == 'apiKey')				
				config.apiKey = c.sys_id + '';
			if (c.getValue('name') == 'userName')
				config.userName = c.sys_id + '';     
		}
		return config;
	},

    type: 'DomainToolsConfiguration'
};
